volatile SocketMethod SocketServer::socket_method = NOSOCKET;

SocketServer::SocketServer()
{
    init_flag = false;
    timing_check = NULL;
    comm_base    = NULL;
    accept_base  = NULL;
    listener     = NULL;
    comm_thread_id = 0;
    accept_thread_id = 0;
}

SocketServer::~SocketServer()
{
    init_flag = false;
    timing_check = NULL;
    comm_base    = NULL;
    accept_base  = NULL;
    listener     = NULL;    
    comm_thread_id = 0;
    accept_thread_id = 0;
}

