#include "rpc_conf.h"
#include "rpc_conf_base.h"

RPCConfig *RPCConfig::m_rpc_config_ptr = NULL;

RPCConfig::RPCConfig()
{
    m_rpc_config_ptr = NULL;
}

RPCConfig::~RPCConfig()
{
    if(m_rpc_config_ptr)
        delete m_rpc_config_ptr;
    m_rpc_config_ptr = NULL;
}

RPCConfig *RPCConfig::create(void)
{
    return new RPCConfigBase();
}

RPCConfig *RPCConfig::getInstance(void)
{
    if(m_rpc_config_ptr)
        return m_rpc_config_ptr;
    m_rpc_config_ptr = new RPCConfigBase();
    return m_rpc_config_ptr;
}

